import { Component } from '@angular/core';
import { AdminService } from './admin.service';  // Assurez-vous que le service AdminService est importé

@Component({
  selector: 'app-admin-dashboard',
  templateUrl: './admin-dashboard.component.html',
  styleUrls: ['./admin-dashboard.component.css']
})
export class AdminDashboardComponent {
  sections = [
    { key: 'banner', title: 'Bannière' },
    { key: 'event', title: 'Événement' },
    { key: 'ad', title: 'Publicité' },
    { key: 'video', title: 'Vidéo' },
    { key: 'product', title: 'Produit' },
    { key: 'testimonial', title: 'Témoignage' },
    { key: 'news', title: 'Actualité' }
  ];

  banner = { title: '', image: null, description: '', ctaLink: '' };
  event = { title: '', image: null, description: '', date: '' };
  ad = { title: '', image: null, description: '', link: '' };
  video = { title: '', description: '', videoUrl: '' };
  product = { name: '', image: null, price: 0, description: '' };
  testimonial = { author: '', image: null, testimonialText: '' };
  news = { title: '', content: '', date: '', image: null };

  visibleForms = { banner: false, event: false, ad: false, video: false, product: false, testimonial: false, news: false };

  constructor(private adminService: AdminService) {}

  toggleForm(sectionKey: string, event: Event): void {
    event.stopPropagation();
    this.visibleForms[sectionKey] = !this.visibleForms[sectionKey];
  }

  isFormVisible(sectionKey: string): boolean {
    return this.visibleForms[sectionKey];
  }

  onFileChange(event: Event, section: string): void {
    const file = (event.target as HTMLInputElement).files?.[0];
    if (file) {
      this[section] = file;
    }
  }

  submitForm(sectionKey: string): void {
    const formData = new FormData();

    switch (sectionKey) {
      case 'banner':
        formData.append('title', this.banner.title);
        formData.append('description', this.banner.description);
        formData.append('ctaLink', this.banner.ctaLink);
        if (this.banner.image) formData.append('image', this.banner.image, this.banner.image.name);
        this.submitRequest(formData, this.adminService.addBanner, 'Bannière');
        break;

      case 'event':
        formData.append('title', this.event.title);
        formData.append('description', this.event.description);
        formData.append('date', this.event.date);
        if (this.event.image) formData.append('image', this.event.image, this.event.image.name);
        this.submitRequest(formData, this.adminService.addEvent, 'Événement');
        break;

      case 'ad':
        formData.append('title', this.ad.title);
        formData.append('description', this.ad.description);
        formData.append('link', this.ad.link);
        if (this.ad.image) formData.append('image', this.ad.image, this.ad.image.name);
        this.submitRequest(formData, this.adminService.addAd, 'Publicité');
        break;

      case 'video':
        formData.append('title', this.video.title);
        formData.append('description', this.video.description);
        formData.append('videoUrl', this.video.videoUrl);
        this.submitRequest(formData, this.adminService.addVideo, 'Vidéo');
        break;

      case 'product':
        formData.append('name', this.product.name);
        formData.append('price', this.product.price.toString());
        formData.append('description', this.product.description);
        if (this.product.image) formData.append('image', this.product.image, this.product.image.name);
        this.submitRequest(formData, this.adminService.addProduct, 'Produit');
        break;

      case 'testimonial':
        formData.append('author', this.testimonial.author);
        formData.append('testimonialText', this.testimonial.testimonialText);
        if (this.testimonial.image) formData.append('image', this.testimonial.image, this.testimonial.image.name);
        this.submitRequest(formData, this.adminService.addTestimonial, 'Témoignage');
        break;

      case 'news':
        formData.append('title', this.news.title);
        formData.append('content', this.news.content);
        formData.append('date', this.news.date);
        if (this.news.image) formData.append('image', this.news.image, this.news.image.name);
        this.submitRequest(formData, this.adminService.addNews, 'Actualité');
        break;

      default:
        break;
    }

    this.resetForm(sectionKey);
    this.visibleForms[sectionKey] = false;
  }

  resetForm(sectionKey: string): void {
    switch (sectionKey) {
      case 'banner':
        this.banner = { title: '', image: null, description: '', ctaLink: '' };
        break;
      case 'event':
        this.event = { title: '', image: null, description: '', date: '' };
        break;
      case 'ad':
        this.ad = { title: '', image: null, description: '', link: '' };
        break;
      case 'video':
        this.video = { title: '', description: '', videoUrl: '' };
        break;
      case 'product':
        this.product = { name: '', image: null, price: 0, description: '' };
        break;
      case 'testimonial':
        this.testimonial = { author: '', image: null, testimonialText: '' };
        break;
      case 'news':
        this.news = { title: '', content: '', date: '', image: null };
        break;
      default:
        break;
    }
  }

  submitRequest(formData: FormData, serviceMethod: Function, sectionTitle: string): void {
    serviceMethod(formData).subscribe({
      next: (response) => {
        console.log(`${sectionTitle} ajouté avec succès!`);
      },
      error: (error) => {
        console.error(`Erreur lors de l'ajout du ${sectionTitle}`, error);
      }
    });
  }
}
