import { Component } from '@angular/core';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css']
})
export class DashboardComponent {
  activeForm: string | null = null;

  // Fonction pour basculer l'affichage des formulaires
  toggleForm(formName: string): void {
    // Si le formulaire actif est déjà celui sur lequel on a cliqué, on le ferme
    this.activeForm = this.activeForm === formName ? null : formName;
  }

  // Vérifie si un formulaire spécifique doit être affiché
  isFormVisible(formName: string): boolean {
    return this.activeForm === formName;
  }
}
