import { Component } from '@angular/core';

@Component({
  selector: 'app-admin-dashboard',
  templateUrl: './admin-dashboard.component.html',
  styleUrls: ['./admin-dashboard.component.css']
})
export class AdminDashboardComponent {
  private sectionState: { [key: string]: boolean } = {};
  private formState: { [key: string]: boolean } = {};

  toggleForm(section: string, event: MouseEvent) {
    event.stopPropagation(); // Prevents the section from expanding/collapsing
    this.formState[section] = !this.formState[section];
  }

  isFormVisible(section: string): boolean {
    return this.formState[section] || false;
  }

  expandSection(section: string, event: MouseEvent) {
    // Prevent form toggle when section is clicked
    if (event.target !== event.currentTarget) return;
    this.sectionState[section] = !this.sectionState[section];
  }

  isExpanded(section: string): boolean {
    return this.sectionState[section] || false;
  }
}
