import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class AdminService {
  private apiUrl = 'http://localhost:3000/api'; // Remplace cette URL par ton backend

  constructor(private http: HttpClient) {}

  addBanner(formData: FormData): Observable<any> {
    return this.http.post(`${this.apiUrl}/banniere`, formData);
  }

  addEvent(formData: FormData): Observable<any> {
    return this.http.post(`${this.apiUrl}/evenement`, formData);
  }

  addAd(formData: FormData): Observable<any> {
    return this.http.post(`${this.apiUrl}/publicite`, formData);
  }

  addVideo(formData: FormData): Observable<any> {
    return this.http.post(`${this.apiUrl}/video`, formData);
  }

  addProduct(formData: FormData): Observable<any> {
    return this.http.post(`${this.apiUrl}/produit`, formData);
  }

  addTestimonial(formData: FormData): Observable<any> {
    return this.http.post(`${this.apiUrl}/temoignage`, formData);
  }

  addNews(formData: FormData): Observable<any> {
    return this.http.post(`${this.apiUrl}/actualite`, formData);
  }
}
