import { Component } from '@angular/core';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css']
})
export class DashboardComponent {
  // Formulaires de chaque section
  banner = { title: '', image: null as File | null, description: '', ctaLink: '' };
  event = { title: '', image: null as File | null, description: '', date: '' };
  ad = { title: '', image: null as File | null, description: '', link: '' };
  product = { name: '', image: null as File | null, price: 0, description: '' };
  testimonial = { author: '', image: null as File | null, testimonialText: '' };
  news = { title: '', content: '', date: '', image: null as File | null };
  video = {
    title: '',
    videoUrl: '',
    description: ''
  };



  // Liste des sections √† afficher dans le HTML
  sections = [
    { key: 'banner', title: 'Banni√®re' },
    { key: 'event', title: '√âv√©nement' },
    { key: 'ad', title: 'Publicit√©' },
    { key: 'video', title: 'Vid√©o' },
    { key: 'product', title: 'Produit' },
    { key: 'testimonial', title: 'T√©moignage' },
    { key: 'news', title: 'Actualit√©' }
  ];

  // √âtat d'affichage des formulaires
  visibleForms: { [key: string]: boolean } = {};

  // Affiche ou cache le formulaire d'une section
  toggleForm(sectionKey: string, event: Event): void {
    event.stopPropagation(); // emp√™che le d√©clenchement de expandSection
    this.visibleForms[sectionKey] = !this.visibleForms[sectionKey];
  }

  // V√©rifie si un formulaire est visible
  isFormVisible(sectionKey: string): boolean {
    return this.visibleForms[sectionKey];
  }

  // G√®re l'upload de fichier
  onFileChange(event: Event, field: string): void {
    const input = event.target as HTMLInputElement;
    const file = input.files?.[0] || null;

    switch (field) {
      case 'bannerImage':
        this.banner.image = file;
        break;
      case 'eventImage':
        this.event.image = file;
        break;
      case 'adImage':
        this.ad.image = file;
        break;
      case 'productImage':
        this.product.image = file;
        break;
      case 'testimonialImage':
        this.testimonial.image = file;
        break;
      case 'newsImage':
        this.news.image = file;
        break;
    }
  }

  // Soumission des formulaires
  submitForm(sectionKey: string): void {
    switch (sectionKey) {
      case 'banner':
        console.log('Banni√®re envoy√©e :', this.banner);
        this.resetForm(sectionKey);
        break;
      case 'event':
        console.log('√âv√©nement envoy√© :', this.event);
        this.resetForm(sectionKey);
        break;
      case 'ad':
        console.log('Publicit√© envoy√©e :', this.ad);
        this.resetForm(sectionKey);
        break;
      case 'video':
        console.log('Vid√©o envoy√©e :', this.video);
        this.resetForm(sectionKey);
        break;
      case 'product':
        console.log('Produit envoy√© :', this.product);
        this.resetForm(sectionKey);
        break;
      case 'testimonial':
        console.log('T√©moignage envoy√© :', this.testimonial);
        this.resetForm(sectionKey);
        break;
      case 'news':
        console.log('Actualit√© envoy√©e :', this.news);
        this.resetForm(sectionKey);
        break;
    }

    // Optionnel : cacher le formulaire apr√®s soumission
    this.visibleForms[sectionKey] = false;
  }

  // R√©initialise le formulaire
  resetForm(sectionKey: string): void {
    switch (sectionKey) {
      case 'banner':
        this.banner = { title: '', image: null, description: '', ctaLink: '' };
        break;
      case 'event':
        this.event = { title: '', image: null, description: '', date: '' };
        break;
      case 'ad':
        this.ad = { title: '', image: null, description: '', link: '' };
        break;
      case 'video':
        this.video = { title: '', description: '', videoUrl: '' };
        break;
      case 'product':
        this.product = { name: '', image: null, price: 0, description: '' };
        break;
      case 'testimonial':
        this.testimonial = { author: '', image: null, testimonialText: '' };
        break;
      case 'news':
        this.news = { title: '', content: '', date: '', image: null };
        break;
    }
  }

  // Pour l'expansion du header (si tu veux autre chose que juste toggle)
  expandSection(sectionKey: string, event: Event): void {
    event.stopPropagation();
    // On pourrait ajouter une logique d‚Äôanimation ici
  }
}
