// contenu-accueil.component.ts
import { Component, OnInit } from '@angular/core';
import { VisibilityService } from '../services/visibility.service';  // Importer le service

@Component({
  selector: 'app-contenu-accueil',
  templateUrl: './contenu-accueil.component.html',
  styleUrls: ['./contenu-accueil.component.css']
})
export class ContenuAccueilComponent implements OnInit {
  isBannerVisible: boolean = false;

  constructor(private visibilityService: VisibilityService) {}

  ngOnInit(): void {
    // S'abonner à la visibilité du banner via le service
    this.visibilityService.isVisible$.subscribe(visible => {
      this.isBannerVisible = visible;
    });
  }
}
