import { Component } from '@angular/core';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css']
})
export class DashboardComponent {
  showBannerForm = false;
  showEventForm = false;
  showAdForm = false;
  showVideoForm = false;
  showProductForm = false;
  showTestimonialForm = false;
  showNewsForm = false;

  // Méthodes pour basculer la visibilité des formulaires
  toggleBannerForm() {
    this.showBannerForm = !this.showBannerForm;
  }

  toggleEventForm() {
    this.showEventForm = !this.showEventForm;
  }

  toggleAdForm() {
    this.showAdForm = !this.showAdForm;
  }

  toggleVideoForm() {
    this.showVideoForm = !this.showVideoForm;
  }

  toggleProductForm() {
    this.showProductForm = !this.showProductForm;
  }

  toggleTestimonialForm() {
    this.showTestimonialForm = !this.showTestimonialForm;
  }

  toggleNewsForm() {
    this.showNewsForm = !this.showNewsForm;
  }
}
