import { Component, OnInit, OnDestroy } from '@angular/core';
import { VisibilityService } from 'src/app/services/visibility.service';
import { Subject } from 'rxjs';
import { takeUntil } from 'rxjs/operators';

@Component({
  selector: 'app-accueil',
  templateUrl: './accueil.component.html',
  styleUrls: ['./accueil.component.css']
})
export class AccueilComponent implements OnInit, OnDestroy {
  isBannerVisible = false;

  private readonly destroy$ = new Subject<void>();

  constructor(private readonly visibilityService: VisibilityService) {}

  ngOnInit(): void {
    // Abonnement Ã  la visibilitÃ© du banner
    this.visibilityService.isVisible$
      .pipe(takeUntil(this.destroy$))
      .subscribe((visibility: boolean) => {
        this.isBannerVisible = visibility;
      });
  }

  // MÃ©thode pour basculer la visibilitÃ©
  toggleBannerVisibility(): void {
    this.visibilityService.toggleVisibility();
  }

  ngOnDestroy(): void {
    // Nettoyage de l'abonnement lors de la destruction du composant
    this.destroy$.next();
    this.destroy$.complete();
  }
}
<app-header></app-header>

<div class="container">
  <app-banner *ngIf="isBannerVisible" class="banner" [ngClass]="{'banner-visible': isBannerVisible}"></app-banner>
  <!-- <app-promotion-banner></app-promotion-banner> -->
  <div class="content-accueil" [ngClass]="{'content-accueil-expanded': isBannerVisible}">
    <app-contenu-accueil></app-contenu-accueil>
  </div>
</div>


<app-footer></app-footer>

<div class="support-button">
