import { Component } from '@angular/core';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css']
})
export class DashboardComponent {
  sections = [
    { key: 'banner', title: 'Bannière' },
    { key: 'event', title: 'Événement' },
    { key: 'ad', title: 'Publicité' },
    { key: 'video', title: 'Vidéo' },
    { key: 'product', title: 'Produit' },
    { key: 'testimonial', title: 'Témoignage' },
    { key: 'news', title: 'Actualité' }
  ];

  expandedSection: string | null = null;
  visibleForms: { [key: string]: boolean } = {};

  expandSection(section: string, event: Event) {
    event.stopPropagation();
    this.expandedSection = this.expandedSection === section ? null : section;
  }

  isExpanded(section: string): boolean {
    return this.expandedSection === section;
  }

  toggleForm(section: string, event: Event) {
    event.stopPropagation();
    this.visibleForms[section] = !this.visibleForms[section];
  }

  isFormVisible(section: string): boolean {
    return this.visibleForms[section] || false;
  }
}
