import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class AdminService {
  private apiUrl = 'http://localhost:3000/api'; // Remplace cette URL si besoin

  constructor(private http: HttpClient) {}

  // Ajouter une bannière
  addBanner(formData: FormData): Observable<any> {
    return this.http.post(`${this.apiUrl}/bannieres`, formData);
  }

  // Ajouter un événement
  addEvent(formData: FormData): Observable<any> {
    return this.http.post(`${this.apiUrl}/evenements`, formData);
  }

  // Ajouter une publicité
  addAd(formData: FormData): Observable<any> {
    return this.http.post(`${this.apiUrl}/publicites`, formData);
  }

  // Ajouter une vidéo
  addVideo(formData: FormData): Observable<any> {
    return this.http.post(`${this.apiUrl}/videos`, formData);
  }

  // Ajouter un produit
  addProduct(formData: FormData): Observable<any> {
    return this.http.post(`${this.apiUrl}/produits`, formData);
  }

  // Ajouter un témoignage
  addTestimonial(formData: FormData): Observable<any> {
    return this.http.post(`${this.apiUrl}/temoignages`, formData);
  }

  // Ajouter une actualité
  addNews(formData: FormData): Observable<any> {
    return this.http.post(`${this.apiUrl}/actualites`, formData);
  }
}
